insert into parameters(name,unit,description) values ('Pressão atmosférica','hPa','Pressão atmosférica é a pressão exercida pela camada de moléculas de ar sobre a superfície. A pressão é a força exercida por unidade de área, neste caso a força exercida pelo ar em um determinado ponto da superfície.');
