insert into parameters(name,unit,description) values ('Vento velocidade','m/seg','A velocidade do vento representa a distância percorrida pela massa de ar num certo intervalo de tempo. A média de todas as velocidades do vento calculadas para cada intervalo do modelo da hora anterior.');
